package Atividade;

public class Calculos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numero1 = 10;
		int numero2 = 10;
		int soma = numero1 + numero2;
		int divisao = numero1 / numero2;
		int subtracao = numero1 - numero2;
		int multiplicacao = numero1 * numero2;
		int restoDaDivisao = numero1 % numero2;
		int calculo = (10 + 10) * 10 / 10;
		System.out.println("resultado da soma : " + soma);
		System.out.println("Somando 2 numeros: " + (numero1 + numero2));
		System.out.println("Divisão de dois numeros: " + divisao);
		System.out.println("Subtraindo 2 numeros:" + (numero1 - numero2) );
		System.out.println("multiplicando 2 numeros :" + multiplicacao);
		System.out.println("Resto da divisao:" + restoDaDivisao );
		System.out.println("calculando:" + calculo );
		
		
	}

}
